import React from 'react';
// import './Movie.css';
class Movie extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        count: 0
      };
      this.increment = this.increment.bind(this);
    };
    increment() {
        this.setState(state => ({
          count: state.count + 1
        }));
      }
    render() {
      return (
         // <div className="container-fluid" style={{margin: '20px'}}>
         <div className="col-md-5" style={{margin: '10px', left: '100px', width:'20%'}}>
         <div class="card-lg shadow-dark" style={{  backgroundColor:'whitesmoke', width: '70%'}}>
             <div className="card-image">                       
                 { this.props.image == null ? <img className="" src={``} alt="Card image cap" style={{ width: "100%",
                     height: 360}}/> : <img className="" src={`http://image.tmdb.org/t/p/w185${this.props.image}`} alt="Card image cap" style={{ width: "100%",
                     height: 360}} />}                         
             </div>
             <div class="card-content shadow-dark" style={{height: '20px', margin: '30px'}}>                    
                 <p><a  href="#" onClick={() => this.viewMovieInfo(this.props.movieId)}>View Details</a></p>                  
             </div>  
             <div className="footer-card" style={{backgroundColor: 'whitesmoke', height: '80px', width: '100%'}}>
                 <div className="button" style={{position: 'relative', padding: '15px'}}>
                     {/* <a class="waves-effect waves-light btn">Delete</a> */}
                     <button className=" btn btn-secondary btn-sm m-2" onClick={() => this.deleteHandler(this.props.id.i)}>
                         Delete
                     </button> 
                     <button className='inc' onClick={this.increment}>
                      <p> {this.state.count} Like </p>
                     </button>     
                 </div>                        
             </div>        
         </div>
     {/* </div> */}
     <formatDate />
 </div>
      )
    }
}
export default Movie;
